mapper.select:
SELECT MEM_ID
FROM MEMBER
WHERE MEM_ID = #{memId};

mapper.insert:
INSERT INTO MEMBER(
MEM_ID
, MEM_PW
, MEM_NAME
, MEM_TEL
, MEM_EMAIL
, CITIZEN_NUM
, POST
, MEM_ADDR
, ADDR_DETAIL) VALUES(
#{memId}
, #{memPw}
, #{memName}
, #{memTel}
, #{memEmail}
, #{citizenNum}
, #{post}
, #{memAddr}
, #{addrDetail});

mapper.select:
SELECT *
    FROM MEMBER
WHERE MEM_ID = #{memId}
AND MEM_PW = #{memPw}

mapper.insert:
INSERT INTO INSERT_ONE(
MEM_ID
, MEM_PW
, MEM_NAME
, MEM_TEL
, MEM_EMAIL
, CITIZEN_NUM
, POST
, MEM_ADDR
, ADDR_DETAIL) VALUES(
#{memId}
, #{memPw}
, #{memName}
, #{memTel}
, #{memEmail}
, #{citizenNum}
, #{post}
, #{memAddr}
, #{addrDetail});
